import Link from 'next/link';
import Layout from '../components/Layout';

const Homepage = () => (
  <Layout title="Homepage">
    <div className="cover">
      <div className="hello">
        <h1>BRILLIANTLY DESIGNED AND BUILT.</h1>
        <div className="subtitle">RHUWELL SAMANO - FULL STACK SOFTWARE ENGINEER</div>
        <Link href="/about">
          <a className="view-more">LEARN MORE</a>
        </Link>
      </div>
    </div>

    <div className="section-container">
      <div className="left-box">
        <div className="latest-work">
          <h2>Latest Work</h2>

          <div className="">
            <Link href="/projects">
              <a className="view-more">View All</a>
            </Link>
          </div>
        </div>
      </div>

      <div className="right-box">
        PIC PLACEHOLDER
      </div>
    </div>

    <style jsx>{`
      .cover {
        position: relative;
        min-height: 100vh;
        background: transparent url(/static/cover.jpg) no-repeat center center;
        background-size: cover;
        background-attachment: fixed;
        background-position: center;
        background-repeat: no-repeat;
        background-size: cover;
        text-align: left;
      }
      .hello {
        color: #FFF;
        line-height: 1.4;
        display: inline-block;
        position: relative;
        top: 200px;
        left: 50px;
        padding-left: 80px;
        padding-right: 80px;
        opacity: 100%;

      }
      .hello h1 {
        line-height: 1.0;
        font-family: 'Galano';
        color: #FFF;
        font-size: 92px;
        margin: 0 0 0px 0;
        padding: 0px;
      }
      .subtitle {
        font-family: "Raleway";
        margin-left: 5px;
      }
      a.view-more {
        font-family: 'Larsseit';
        display: inline-block;
        text-transform: uppercase;
        font-size: 16px;
        background: #342d22;
        margin-left: 5px;
        margin-top: 30px;
        padding: 10px 40px;
        top: 25px;
        color: #FFF;
        border-radius: 5px;
      }
      a.view-more:hover {
        color: #FECE00;
      }
      .latest-work {
        font-family: 'Galano';
        text-align: center;
        padding: 30px 0;
        margin-bottom: 60px;
      }
      @media (max-width: 480px) {
        .hello {
          font-family: 'Galano Grotesque';
          font-size: 18px;
          padding: 20px;
        }
        h1 {
          font-size: 28px;
        }
      }
      .section-container {
        height: 100vh;
        display: block;
        max-width: 100%;
      }
      .left-box {
        box-sizing: border-box;
        float: left;
        max-width: 60%;
      }
      .right-box {
        box-sizing: border-box;
        float: right;
        max-width 60%;
      }
    `}</style>
  </Layout>
);

export default Homepage;
