import Link from 'next/link';
import Layout from '../components/Layout';

const Homepage = () => (
  <Layout title="Homepage">
    <div className="cover">
      <div className="hello">
        <h1>BRILLIANTLY DESIGNED AND BUILT.</h1>
        <div className="subtitle">RHUWELL SAMANO - FULL STACK SOFTWARE ENGINEER</div>
        <Link href="/about">
          <a className="view-more">LEARN MORE</a>
        </Link>
      </div>
    </div>

    <div className="section-container">
      <div className="left-box">
        <div className="about">
          <h2>About.</h2>
          <h3>INSPIRED BY DIFFERENCE.</h3>
          <div className="">
            <Link href="/projects">
              <a className="view-more">View All</a>
            </Link>
          </div>
        </div>
      </div>

      <div className="right-box">
      </div>
    </div>

    <style jsx>{`
      .cover {
        position: relative;
        min-height: 100vh;
        background: transparent url(https://avada.theme-fusion.com/resume/wp-content/uploads/sites/66/2016/07/home_bg.jpg) no-repeat center center;
        background-size: cover;
        background-attachment: fixed;
        background-position: center;
        background-repeat: no-repeat;
        background-size: cover;
        text-align: left;
      }
      .hello {
        color: #FFF;
        line-height: 1.4;
        display: inline-block;
        position: relative;
        top: 200px;
        left: 50px;
        padding-left: 80px;
        padding-right: 80px;
        opacity: 100%;

      }
      .hello h1 {
        line-height: 1.0;
        font-family: 'Galano';
        color: #FFF;
        font-size: 92px;
        margin: 0 0 0px 0;
        padding: 0px;
      }
      .subtitle {
        font-family: "Raleway";
        margin-left: 5px;
      }
      a.view-more {
        font-family: 'Larsseit';
        display: inline-block;
        text-transform: uppercase;
        font-size: 16px;
        background: #FF6600;
        margin-left: 5px;
        margin-top: 30px;
        padding: 10px 40px;
        top: 25px;
        color: #FFF;
        border-radius: 5px;
      }
      a.view-more:hover {
        color: #FECE00;
      }
      .about {
        text-align: left;
        padding: 40px;
      }
      .about h2 {
        font-family: 'Galano';
        font-size: 120px;
        color: #F2F2F2;
        padding-top: 60px;
        margin-bottom: 10px;
        line-height: 0.3;
      }
      .about h3 {
        font-family: 'Raleway';
        font-size: 30px;
        color: #343D4F;
        text-align: left;
        padding: 0px 0;
        margin-bottom: 0px;
      }
      @media (max-width: 480px) {
        .hello {
          font-family: 'Galano Grotesque';
          font-size: 18px;
          padding: 20px;
        }
        h1 {
          font-size: 28px;
        }
      }
      .section-container {
        height: 100vh;
        display: block;
        max-width: 100%;
        margin: 0 auto;
      }
      .left-box {
        box-sizing: border-box;
        background: #FFF;
        float: left;
        width: 60%;
        height: 100%;
      }
      .right-box {
        box-sizing: border-box;
        background: url(https://avada.theme-fusion.com/resume/wp-content/uploads/sites/66/2016/07/avada-resume-about.jpg) no-repeat center center;
        background-size: cover;
        float: right;
        width 40%;
        height: 100%;
      }
    `}</style>
  </Layout>
);

export default Homepage;
